import React from "react";
import {
  Typography,
  Box,
  Card,
  CardContent,
  CardActionArea,
} from "@material-ui/core";

const MyAccordian = (props) => {
  const [show, setShow] = useState(false);

  return (
    <>
      <div>
        <Box p={2}>
          <Card p={3}>
            <CardActionArea>
              <CardContent>
                <Typography>{props.question}</Typography>
                {show && <Typography>{props.ans}</Typography>}
              </CardContent>
            </CardActionArea>
          </Card>
        </Box>
      </div>
    </>
  );
};

export default MyAccordian;
